mapper.update:
UPDATE approval
start_date = #{startDate:VARCHAR} , end_date = #{endDate:VARCHAR} , use_day = #{useDay:INTEGER} , title = #{title} , content = #{content} , memo = #{memo:VARCHAR} , filename1 = #{fileName1:VARCHAR} , filename2 = #{fileName2:VARCHAR} , filename3 = #{fileName3:VARCHAR} , mod_date = sysdate , mod_employee_no = #{modEmployeeNo:VARCHAR}
WHERE doc_no = #{docNo}

UPDATE approval
status = ( SELECT CASE WHEN tp='000' THEN '1' WHEN tp='110' THEN '2' WHEN tp='100' THEN '1' WHEN tp='001' THEN '4' WHEN tp='010' THEN '3' WHEN tp='101' THEN '9' WHEN tp='011' THEN '9' WHEN tp='111' THEN '9' ELSE '0' END FROM ( SELECT MAX(tp1)||MAX(tp2)||MAX(tp3) as tp FROM ( SELECT CASE WHEN status='1' THEN '1' ELSE '0' END as tp1, CASE WHEN status='2' THEN '1' ELSE '0' END as tp2, CASE WHEN status='3' THEN '1' ELSE '0' END as tp3 FROM approval_employee WHERE doc_no = #{docNo} AND gubun='1' ) ) ) , mod_date = sysdate , mod_employee_no = #{modEmployeeNo:VARCHAR}
WHERE doc_no = #{docNo}

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT doc_no
			FROM approval
			WHERE reg_employee_no=#{regEmployeeNo}
			AND status =#{docStatus}
				) countTable
